import './App.css';
import Header from './headers.js';
import React , {useState,useEffect} from 'react';
import Axios from 'axios'
import * as ReactBootstrap from "react-bootstrap";




function App() {
  const [movieName,setmovie] = useState('')
  const [review,setreview] = useState('')  
  const [productos,setProd] = useState([]);
  const [mejoresV,setMejoresV] = useState([]);

  async function componentf(){
    const url = 'http://localhost:3002/transac';
    const response = await fetch(url)
    const data = await response.json()
    console.log(type(data[0]))
    setMejoresV(data[0]);
    g();    
   }


  async function verProd(){
    const url = 'http://localhost:3002/Prod';
    const response = await fetch(url)
    const data = await response.json()
    console.log(type(data))    
    g();    
   }
   const renderVenditors = (venditor,index)=> {
     return (
       <tr> 
        <td> {venditor.Description}</td>
        <td> {venditor.posttime}</td>
      </tr>
     )
   }


  function g() {
    var myVar = setTimeout(alertFunc, 3000);
  }

  function alertFunc() {
    setProd([]);
    setMejoresV([]);
  }

  return (
    <div className="App">            
      <h1> Venditor Demo</h1>
      <div className = 'form'>
        <label>Nombre de pelicula</label>
        <input type = 'text' name = 'movieName' onChange= {(e)=>{
          setmovie(e.target.value)
        }}/>
        <label>Review</label>
        <input type = 'text' name = 'Review' onChange= {(e)=>{
          setreview(e.target.value)
        }}/>                
        <button onClick= {componentf}>Ingresar informaci√≥n </button>      

        <button onClick= {verProd}>Ver productos </button>      



        </div>

        <table className = "table table-dark">
          <thead>
              <tr>
                <th>Description</th>
                <th>posttime</th>
              </tr>
          </thead>
          <tbody>
              {mejoresV.map(renderVenditors)}
          </tbody>
       </table>
      </div>
  );
}

export default App;
