import './App.css';
import React , {useState,useEffect} from 'react';
import Axios from 'axios'
import * as ReactBootstrap from "react-bootstrap";




function App() {
  const [movieName,setmovie] = useState('')
  const [review,setreview] = useState('')  
  const [productos,setProd] = useState([]);
  const [mejoresV,setMejoresV] = useState([]);

  async function componentf(){
    const url = 'http://localhost:3002/ofertas';
    const response = await fetch(url)
    const data = await response.json()    
    setMejoresV(data[0]);
    g();    
   }


  async function verProd(){
    const url = 'http://localhost:3002/Prod';
    const response = await fetch(url)
    const data = await response.json()    
    setProd(data); 
    g();
   }
   const renderVenditors = (venditor,index)=> {
     return (
       <tr> 
        <td> {venditor.Description}</td>
        <td> {venditor.posttime}</td>
      </tr>
     )
   }

  const renderProducts = (products,index)=> {
    return (
      <tr key={`${products.Productid}`} > 
       <td> {products.Name}</td>
       <td> {products.PriceSale}</td>       
     </tr>
    )
  }
  function g() {
    var myVar = setTimeout(alertFunc, 5000);
  }

  function alertFunc() {    
    setProd([]);
    setMejoresV([]);
  }

  return (
    <div className="App">            
      <h1> Venditor Demo</h1>
      <div className = 'form'>        
        </div>        
        <button onClick= {componentf}>Ver Mejores Bids para vendedor</button>  
        <table className = "table table-dark">
          <thead>
              <tr>
                <th>Description</th>
                <th>posttime</th>
              </tr>
          </thead>
          <tbody>
              {mejoresV.map(renderVenditors)}
          </tbody>
       </table>
       <button onClick= {verProd}>Ver productos </button>      
       <table className = "table table-dark">
          <thead>
              <tr>
                <th>Name</th>       
                <th>Description</th>         
              </tr>
          </thead>
          <tbody>       
            {productos.map(renderProducts)}       
          </tbody>
       </table>
      </div>
  );
}

export default App;
